</template>

<script>

import NavBar from './NavBar.vue';
import PostItComponent from './PostItComponent.vue';
import CVItem from './CVItem.vue';
import FooterPage from './FooterPage.vue';

export default {
    name: 'AboutPage',
    components: {
        NavBar,
        PostItComponent,
        CVItem,
        FooterPage
    },
    data() {
        return {
        top: '20px',
        left: '50px',
        isDragging: false,
        startX: 0,
        scrollLeft: 0,
        }
    },
    methods : {
        // au hover d'un élément de la classe orange, changer la couleur de tous les éléments de la classe orange
        changeColor(color) {
            const elements = document.querySelectorAll(`.${color}`);
            elements.forEach(element => {
            if (element.tagName.toLowerCase() === 'svg') {
                // Get the internal document of the SVG
            // Get the internal document of the SVG
                const svgDocument = element;
            // Change the fill color of all path elements
                const paths = svgDocument.querySelectorAll('path');
            paths.forEach(path => {
                path.style.fill = this.getColorForClass(color);
            });
        } else {
            // Update the color property for other elements
            element.style.color = this.getColorForClass(color);
        }
    });
},

    getColorForClass(color) {
        switch (color) {
            case 'orange':
                return '#EA5919';
            case 'blue':
                return '#79C9D8';
            case 'purple':
                return '#5E4A99';
            case 'pink':
                return '#E61B70';
            case 'yellow':
                return '#FBBC19';
            default:
                return '#131412';
        }
    },

    backToNormal() {
    const elements = document.querySelectorAll('.logo');
    
    elements.forEach(element => {
        if (element.tagName.toLowerCase() === 'svg') {
                const svgDocument = element;
                const paths = svgDocument.querySelectorAll('path');
            paths.forEach(path => {
                path.style.fill = '';
        });
        } else {
            // Reset the color property for other elements
            element.style.color = '#131412';
        }
        });
    },

    handleMouseDown(sliderNb, event) {
        console.log('handleMouseDown')
        this.isDragging = true;
        event.preventDefault();
        this.startX = event.pageX - this.$refs['slider' + sliderNb].offsetLeft;
        this.scrollLeft = this.$refs['slider' + sliderNb].scrollLeft;
      },
      handleMouseMove(sliderNb, event) {
        console.log('handleMouseMove')
        if (!this.isDragging) return;
        event.preventDefault();
        const x = event.pageX - this.$refs['sliderWrap' + sliderNb].offsetLeft;
        const walk = (x - this.startX) * 2;
        this.$refs['slider' + sliderNb].scrollLeft = this.scrollLeft - walk;
      },
      handleMouseUp() {
        console.log('handleMouseUp')
        this.isDragging = false;
      },
    },

    mounted() {
        //guetter le survol d'un élément de la classe logo, et changer la couleur de tous les éléments de la classe logo en fonction de sa couleur
        const logos = document.getElementsByClassName('logo');
        for (let i = 0; i < logos.length; i++) {
            logos[i].addEventListener('mouseover', () => {
                this.changeColor(logos[i].classList[1]);
            });
            logos[i].addEventListener('mouseout', () => {
                this.backToNormal();
            });
        }
    }
}
</script>

